mapper.select:


     and id = #{id}
     and order_date = #{orderDate}
     and order_time = #{orderTime}
     and start like concat('%', #{start}, '%')
     and end like concat('%', #{end}, '%')
     and car_type = #{carType}
     and car_no = #{carNo}
     and custmer_id = #{custmerId}
     and custmer_name like concat('%', #{custmerName}, '%')
     and phone_num = #{phoneNum}
     and payment_term = #{paymentTerm}
     and towing_fee = #{towingFee}
     and towcar_no = #{towcarNo}
     and commission = #{commission}
     and za_fee = #{zaFee}
     and approve_status = #{approveStatus}
     and driver_name like concat('%', #{driverName}, '%')
     and user_id = #{userId}
     and company_pay = #{companyPay}
     and return_money = #{returnMoney}
     and refuel_money = #{refuelMoney}
     and refuelcard_money = #{refuelcardMoney}
     and mileage = #{mileage}
     and app_remark = #{appRemark}

order by order_date desc

mapper.update:
update order_manager
order_date = #{orderDate}, order_time = #{orderTime}, start = #{start}, end = #{end}, car_type = #{carType}, car_no = #{carNo}, custmer_id = #{custmerId}, custmer_name = #{custmerName}, phone_num = #{phoneNum}, payment_term = #{paymentTerm}, towing_fee = #{towingFee}, towcar_no = #{towcarNo}, commission = #{commission}, za_fee = #{zaFee}, approve_status = #{approveStatus}, driver_name = #{driverName}, user_id = #{userId}, company_pay = #{companyPay}, return_money = #{returnMoney}, refuel_money = #{refuelMoney}, refuelcard_money = #{refuelcardMoney}, mileage = #{mileage}, remark = #{remark}, app_remark = #{appRemark}, update_time = #{updateTime}, record_money = #{recordMoney}, componyreceiver_money = #{componyReceiverMoney}, driverreceiver_money = #{driverReceiverMoney}, return_amount = #{returnAmount},
where id = #{id}
